.admin-setting{
    overflow-y: auto;
}
.setting-panel {
    width: 1000px;
    height: 350px;
    background-color: #f9f9f9;
    margin: 25px auto;
    border-radius: 20px;
}
.setting-panel li {
    height: 50px;
    font-size: 16px;
    line-height: 50px;
    padding-left: 50px;
    color: #606266;
}
.setting-panel li span:first-child {
    margin-right: 10px;
}
