.add-food{
    overflow-y: auto;
}
.add-food .el-form {
    width: 70%;
    margin: 25px auto;
}
.add-food .form-header {
    text-align: center;
    margin: 10px;
}
.add-food .form {
    min-width: 400px;
    padding: 20px 20px 0 0;
    border: 1px solid #eaeefb;
}
.add_category {
    border-top: 1px solid #eaeefb;
    padding-top: 20px;
}
.add_category_button {
    cursor: pointer;
    text-align: center;
    line-height: 40px;
    transition: all 400ms;
    color: #ccc;
    font-size: 14px;
    border-top: 1px solid #eaeefb;
}
.add_category_button:hover {
    color: #20a0ff;
}
.avatar-uploader .el-upload {
    border: 1px dashed #d9d9d9;
    border-radius: 6px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
}
.avatar-uploader .el-upload:hover {
    border-color: #20a0ff;
}
.avatar-uploader-icon {
    font-size: 28px;
    color: #8c939d;
    width: 120px;
    height: 120px;
    line-height: 120px;
    text-align: center;
}
.avatar {
    width: 120px;
    height: 120px;
    display: block;
}
