import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex);

import {getAdminInfo, getUserInfoList, getUserTotalCount, getShopCategory} from '@/api/index'

export function createStore() {
    return new Vuex.Store({
        state: {
            adminInfo: {},
            userInfoList: [],
            userTotalCount: 0,
            shopCategory: []
        },
        mutations: {
            setAdminInfo(state, payload) {
                state.adminInfo = payload;
            },
            setUserInfoList(state, payload) {
                state.userInfoList = payload;
            },
            setUserTotalCount(state, payload) {
                state.userTotalCount = payload;
            },
            setShopCategory(state, payload) {
                payload.forEach(item => {
                    if (item.sub_categories.length) {
                        let addnew = {
                            value: item.name,
                            label: item.name,
                            children: []
                        };
                        item.sub_categories.forEach((subitem, index) => {
                            if (index === 0) {
                                return;
                            }
                            addnew.children.push({
                                value: subitem.name,
                                label: subitem.name,
                            });
                        });
                        state.shopCategory.push(addnew);
                    }
                });
            }
        },
        actions: {
            fetchAdminInfo({commit}) {
                return getAdminInfo().then(({data}) => {
                    if (data.status === 1) {
                        commit('setAdminInfo', data.data);
                        return data.data;
                    }
                })
            },
            fetchUserInfoList({commit}, {limit, offset}) {
                return getUserInfoList(limit, offset).then(({data}) => {
                    commit('setUserInfoList', data);
                })
            },
            fetchUserTotalCount({commit}) {
                return getUserTotalCount().then(({data}) => {
                    commit('setUserTotalCount', data.count);
                })
            },
            fetchShopCategory({commit}) {
                return getShopCategory().then(({data}) => {
                    commit('setShopCategory', data);
                })
            }
        },
        modules: {}
    })
}

import Vue from 'vue'
import App from './App.vue'
import {createRouter} from './router'
import {createStore} from './store'
import {sync} from 'vuex-router-sync'

import ElementUI from 'element-ui'
import 'element-ui/lib/theme-chalk/index.css'

Vue.use(ElementUI);

Vue.config.productionTip = false;

export function createApp() {
    const router = createRouter();
    const store = createStore();

    sync(store, router);

    if (typeof window !== 'undefined' && window.__INITIAL_STATE__) {
        store.replaceState(window.__INITIAL_STATE__);
    }

    router.beforeEach((to, from, next) => {
        console.log('beforeEach');
        console.log(store.state.adminInfo.user_name,
            store.state.adminInfo.id,
            store.state.adminInfo.create_time,
            store.state.adminInfo.status,
            store.state.adminInfo.city,
            store.state.adminInfo.avatar,
            store.state.adminInfo.admin);
        if (to.path === '/login') {
            next();
        } else {
            if (store.state.adminInfo.user_name) {
                next();
            } else {
                if (router.options.routes.some(route => {return route.path === to.path})) {
                    next('/login');
                } else {
                    next();
                }
            }
        }
    });

    const app = new Vue({
        router,
        store,
        render: h => h(App)
    });

    return {app, router, store}
}

import {createApp} from './app'

export default context => {
    return new Promise((resolve, reject) => {
        const {app, router, store} = createApp();

        if (context.login) {
            store.state.adminInfo = {
                "user_name": "adminkkk",
                "id": 61771,
                "create_time": "2019-12-04 14:56",
                "status": 1,
                "city": "南昌",
                "avatar": "16ecfe82e9b59586.jpg",
                "admin": "管理员"
            };
        }

        router.push(context.url);

        router.onReady(() => {
            const matchedComponents = router.getMatchedComponents();

            Promise.all(matchedComponents.map(Component => {
                if (Component.asyncData) {
                    return Component.asyncData({
                        store,
                        route: router.currentRoute
                    });
                }
            })).then(() => {
                context.state = store.state;

                resolve(app);
            }).catch(reject);
        }, reject)
    })
}
